function BinToDec(divId){createDiv(divId);const funcs=require("../../static/js/funcs");function createDiv(divId){let html='\n            <div class="card p-2">\n            <div class="mb-2">Bin2Dec</div>\n            <div class="d-flex align-items-center">\n              <input type="text" class="form-control form-control-sm col" name="binInput" id="binInput"\n                placeholder="Binary" style="width: 60px;" />\n              <span class="col-auto px-1">TO</span>\n              <input type="text" class="form-control form-control-sm col" name="decInput" id="decInput"\n                placeholder="Decimal" style="width: 60px;" />\n            </div>\n          </div>\n        ';document.getElementById(divId).innerHTML=html}document.getElementById("binInput").addEventListener("keypress",funcs.maskBinary),document.getElementById("decInput").addEventListener("keypress",funcs.maskDecimals),document.getElementById("binInput").addEventListener("keyup",e=>{let dec=document.getElementById("decInput");""!=e.target.value?dec.value=parseInt(e.target.value,2):dec.value=""}),document.getElementById("decInput").addEventListener("keyup",e=>{let bin=document.getElementById("binInput");""!=e.target.value?bin.value=(+e.target.value).toString(2):bin.value=""})}module.exports=BinToDec;